import React, { useState, useEffect } from "react";
import { Routes, Route, Link, useLocation } from "react-router-dom";
import MovieList from "./components/MovieList";
import MovieDetail from "./components/MovieDetail";
import MovieForm from "./components/MovieForm";

export default function App() {
  const [showForm, setShowForm] = useState(false);
  const location = useLocation();

  useEffect(() => {
    setShowForm(false);
  }, [location]);

  return (
    // <div className="min-h-screen bg-gradient-to-br from-yellow-300 via-yellow-500 to-yellow-12200 text-gray-900 font-sans">
<div className="min-h-screen bg-gradient-to-br from-yellow-200 via-amber-400 to-orange-600 text-gray-900 font-sans">

      {/* Header */}
      <header className="flex justify-between items-center px-6 py-4 shadow-lg bg-black/30 backdrop-blur-md sticky top-0 z-50">

        {/* Logo */}
        <h1 className="text-3xl font-bold tracking-wide">
          <Link to="/" className="bg-gradient-to-r from-red-600 to-purple-600 bg-clip-text text-transparent hover:opacity-90 transition">
            CineHorizon
          </Link>
        </h1>

        {/* Add Movie Button */}
        <button
          className="px-5 py-2 rounded-lg text-white font-medium shadow-md transition 
                     bg-gradient-to-r from-black to-gray-800 hover:from-gray-900 hover:to-black"
          onClick={() => setShowForm(!showForm)}
        >
          {showForm ? "Close Form" : "Add Movie"}
        </button>
      </header>

      {/* Movie Form Toggle */}
      {showForm && (
        <div className="p-6">
          <MovieForm />
        </div>
      )}

      {/* Main Content */}
      <main className="p-6">
        <Routes>
          <Route path="/" element={<MovieList />} />
          <Route path="/movie/:id" element={<MovieDetail />} />
        </Routes>
      </main>

    </div>
  );
}
