import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../api";

export default function MovieDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    const load = async () => {
      try {
        const res = await API.get(`movies/${id}/`);
        setMovie(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    load();
  }, [id]);

  if (!movie) return <div className="text-center mt-10 text-lg">Loading...</div>;

  const deleteMovie = async () => {
    const confirmDelete = window.confirm(
      "Are you sure you want to delete this movie?"
    );
    if (!confirmDelete) return;

    try {
      await API.delete(`movies/${id}/`);
      navigate("/");
    } catch (err) {
      console.error(err);
      alert("Error deleting movie");
    }
  };

  return (
    <div className="flex flex-col md:flex-row gap-8 p-6 max-w-5xl mx-auto">

      {/* Poster */}
      {movie.poster && (
        <img
          src={movie.poster}
          alt={movie.title}
          className="w-[180px] h-[220px] object-cover rounded-xl shadow-2xl transition-transform duration-300 hover:scale-105"
        />
      )}

      {/* Movie Info */}
      <div className="flex flex-col text-right w-full">

        {/* Gradient Title */}
        <h2 className="text-4xl font-bold mb-3 bg-gradient-to-r from-blue-600 to-cyan-400 bg-clip-text text-transparent">
          {movie.title}
        </h2>

        <p className="text-lg"><span className="text-blue-700 font-semibold">Release date:</span> {movie.release_date || "N/A"}</p>
        <p className="text-lg"><span className="text-blue-700 font-semibold">Rating:</span> {movie.rating || "N/A"}</p>

        <p className="mt-4 text-gray-700 leading-relaxed">{movie.description}</p>

        <button
          onClick={deleteMovie}
          className="mt-6 py-2 px-5 bg-red-600 hover:bg-red-700 text-white text-lg rounded-lg shadow-md transition"
        >
          Delete Movie
        </button>
      </div>
    </div>
  );
}
