import React, {useEffect, useState} from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../api';

export default function MovieDetail(){
  const { id } = useParams();
  const navigate = useNavigate();
  const [movie, setMovie] = useState(null);

  useEffect(()=>{
    const load = async () =>{
      try{
        const res = await API.get(`movies/${id}/`);
        setMovie(res.data);
      }catch(err){ 
        console.error(err);
      }
    };
    load();
  },[id]);

  if(!movie) return <div className="text-center mt-10 text-xl font-semibold text-gray-600">Loading...</div>;

  const deleteMovie = async () =>{
    const confirmDelete = window.confirm("Are you sure you want to delete this movie?");
    if(!confirmDelete) return;

    try{
      await API.delete(`movies/${id}/`);
      navigate("/"); 
    }catch(err){
      console.error(err);
      alert("Error deleting movie");
    }
  };

  return (
    <div className="max-w-5xl mx-auto p-6 flex flex-col md:flex-row gap-10 bg-white/40 backdrop-blur-md shadow-2xl rounded-2xl border border-gray-200 mt-10">

      {movie.poster && (
        <img 
          src={movie.poster} 
          alt={movie.title}
          className="w-64 h-80 object-cover rounded-xl shadow-lg hover:scale-105 transition-transform duration-300"
        />
      )}

      <div className="flex flex-col">

        <h2 className="text-4xl font-extrabold mb-3 bg-gradient-to-r from-indigo-600 via-purple-500 to-pink-500 bg-clip-text text-transparent drop-shadow-sm">
          {movie.title}
        </h2>

        <p className="text-lg text-gray-800"><span className="font-semibold text-indigo-700">Release date:</span> {movie.release_date || 'N/A'}</p>
        <p className="text-lg text-gray-800"><span className="font-semibold text-indigo-700">Rating:</span> {movie.rating || 'N/A'}</p>

        <p className="mt-4 text-gray-700 leading-relaxed">
          {movie.description}
        </p>

        <button 
          className="mt-6 px-6 py-3 bg-gradient-to-r from-red-600 to-red-700 hover:from-red-700 hover:to-red-800 text-white font-semibold rounded-lg shadow-lg transition-all duration-200"
          onClick={deleteMovie}
        >
          Delete Movie
        </button>
      </div>
    </div>
  );
}
