import React, { useEffect, useState } from 'react';
import API from '../api';
import { Link } from 'react-router-dom';

export default function MovieList() {
  const [movies, setMovies] = useState([]);
  const [page, setPage] = useState(1);
  const [next, setNext] = useState(null);
  const [prev, setPrev] = useState(null);

  const fetchMovies = async (p = 1) => {
    try {
      const res = await API.get(`movies/?page=${p}`);
      setMovies(res.data.results);
      setNext(res.data.next);
      setPrev(res.data.previous);
      setPage(p);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchMovies(1);
  }, []);

  useEffect(() => {
    const handler = () => fetchMovies(1);
    window.addEventListener('movie:added', handler);
    return () => window.removeEventListener('movie:added', handler);
  }, []);

  return (
    <div className="min-h-screen px-6">

      {/* Title */}
      <h2 className="text-4xl font-bold mb-8 text-center bg-gradient-to-r from-red-700 to-yellow-700 text-transparent bg-clip-text">
        Movies Catalog
      </h2>

      {/* Movie Card Grid */}
      {/* <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 xl:grid-cols-4 gap-8">
        {movies.map(m => (
          <div
            key={m.id}
            className="rounded-2xl bg-black/40 backdrop-blur-md shadow-lg hover:shadow-xl transition transform hover:-translate-y-1 p-4 border border-yellow-500/30"
          >
            <Link to={`/movie/${m.id}`}>
              {m.poster ? (
                <img
                  src={m.poster}
                  alt={m.title}
                  className="w-full h-56 object-cover rounded-lg shadow-md"
                />
              ) : (
                <div className="w-full h-56 bg-gray-300 rounded-lg flex items-center justify-center text-gray-600">
                  No Image
                </div>
              )}

              <h3 className="mt-4 text-xl font-semibold text-gray-900 dark:text-white">
                {m.title}
              </h3>
            </Link>

            <p className="text-sm mt-2 text-gray-800 dark:text-gray-300">
              Rating: <span className="font-semibold text-red-600">{m.rating || 'N/A'}</span>
            </p>
          </div>
        ))}
      </div> */}


      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 xl:grid-cols-4 gap-10">
  {movies.map(m => (
    <div key={m.id} className="movie-card hover-glow">
      <Link to={`/movie/${m.id}`}>
        {m.poster ? (
          <img src={m.poster} alt={m.title} className="movie-img" />
        ) : (
          <div className="movie-img flex items-center justify-center text-gray-400">
            No Image
          </div>
        )}
        <h3 className="movie-title">{m.title}</h3>
      </Link>
      <p className="rating-text">
        Rating: <span className="rating-value">{m.rating || "N/A"}</span>
      </p>
    </div>
  ))}
</div>


      {/* Pagination */}
      {/* <div className="mt-10 flex items-center justify-center gap-4">
        <button
          onClick={() => fetchMovies(page - 1)}
          disabled={!prev}
          className={`px-4 py-2 rounded-lg text-white font-medium transition 
            ${prev ? "bg-gray-900 hover:bg-black shadow-md" : "bg-gray-500 cursor-not-allowed"}`}
        >
          Previous
        </button>

        <span className="text-lg font-semibold text-gray-900 dark:text-white">
          Page {page}
        </span>

        <button
          onClick={() => fetchMovies(page + 1)}
          disabled={!next}
          className={`px-4 py-2 rounded-lg text-white font-medium transition 
            ${next ? "bg-gray-900 hover:bg-black shadow-md" : "bg-gray-500 cursor-not-allowed"}`}
        >
          Next
        </button>
      </div> */}
      <div className="mt-10 flex items-center justify-center gap-4">
  <button
    onClick={() => fetchMovies(page - 1)}
    disabled={!prev}
    className={`pagination-btn ${
      prev ? "bg-gray-800 hover:bg-black" : "bg-gray-600 cursor-not-allowed"
    }`}
  >
    Previous
  </button>

  <span className="text-lg font-semibold">Page {page}</span>

  <button
    onClick={() => fetchMovies(page + 1)}
    disabled={!next}
    className={`pagination-btn ${
      next ? "bg-gray-800 hover:bg-black" : "bg-gray-600 cursor-not-allowed"
    }`}
  >
    Next
  </button>
</div>

    </div>
  );
}
