import React, {useEffect, useState} from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../api';
// REMOVE this line: import '../styles/MovieDetail.css';

export default function MovieDetail(){
  const { id } = useParams();
  const navigate = useNavigate();
  const [movie, setMovie] = useState(null);

  useEffect(()=>{
    const load = async () =>{
      try{
        const res = await API.get(`movies/${id}/`);
        setMovie(res.data);
      }catch(err){ 
        console.error(err);
      }
    };
    load();
  },[id]);

  if(!movie) return <div className="text-center py-10 text-xl font-semibold text-gray-600">Loading...</div>;

  const deleteMovie = async () =>{
    const confirmDelete = window.confirm("Are you sure you want to delete this movie?");
    if(!confirmDelete) return;

    try{
      await API.delete(`movies/${id}/`);
      navigate("/"); 
    }catch(err){
      console.error(err);
      alert("Error deleting movie");
    }
  };

  return (
    <div className="max-w-5xl mx-auto p-6 flex flex-col md:flex-row gap-10 bg-gradient-to-r from-slate-900 to-black text-white rounded-2xl shadow-2xl mt-10 border border-gray-700">

      {/* Poster */}
      {movie.poster && (
        <img 
          src={movie.poster} 
          alt={movie.title} 
          className="w-60 h-80 object-cover rounded-xl shadow-xl hover:scale-105 transition duration-300"
        />
      )}

      {/* Info Section */}
      <div className="flex flex-col justify-center text-left space-y-3">

        <h2 className="text-4xl font-bold bg-gradient-to-r from-indigo-400 to-cyan-400 bg-clip-text text-transparent drop-shadow-lg">
          {movie.title}
        </h2>

        <p className="text-lg"><span className="font-bold text-cyan-300">Release Date:</span> {movie.release_date || 'N/A'}</p>

        <p className="text-lg"><span className="font-bold text-cyan-300">Rating:</span> {movie.rating || 'N/A'}</p>

        <p className="text-gray-300 leading-relaxed text-md mt-3">
          {movie.description}
        </p>

        <button 
          className="mt-5 bg-red-600 hover:bg-red-700 text-white px-6 py-2 rounded-lg font-semibold shadow-lg hover:shadow-red-500/50 transition-all duration-300"
          onClick={deleteMovie}
        >
          Delete Movie
        </button>

      </div>
    </div>
  );
}
