import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../api";
import '../styles/MovieDetail.css';

export default function MovieDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [movie, setMovie] = useState(null);

  const fetchMovie = async () => {
    try {
      const res = await API.get(`movies/${id}/`);
      setMovie(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const deleteMovie = async () => {
    if (window.confirm("Are you sure you want to delete this movie?")) {
      try {
        await API.delete(`movies/${id}/`);
        navigate("/");
      } catch (err) {
        console.error(err);
        alert("Error deleting movie");
      }
    }
  };

  useEffect(() => {
    fetchMovie();
  }, [id]);

  if (!movie) return <div>Loading...</div>;

  return (
    <div className="movie-detail">
      {movie.poster && <img src={`http://127.0.0.1:8000${movie.poster}`} alt={movie.title} />}
      <h2>{movie.title}</h2>
      <p><strong>Release date:</strong> {movie.release_date}</p>
      <p><strong>Rating:</strong> {movie.rating}</p>
      <p>{movie.description}</p>
      <button onClick={deleteMovie} style={{ background: "red", color: "#fff", padding: "8px 12px", border: "none", borderRadius: "4px", cursor: "pointer" }}>Delete Movie</button>
    </div>
  );
}
