import React, { useEffect, useState } from 'react';
import API from '../api';
import MovieForm from './MovieForm';
import { Link } from 'react-router-dom';
import '../styles/MovieList.css';

export default function MovieList(){
  const [movies, setMovies] = useState([]);
  const [showForm, setShowForm] = useState(false);

  const fetchMovies = async () => {
    try {
      const res = await API.get('movies/');
      setMovies(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => { fetchMovies(); }, []);

  // Refresh when a movie is added
  useEffect(() => {
    const handler = () => fetchMovies();
    window.addEventListener('movie:added', handler);
    return () => window.removeEventListener('movie:added', handler);
  }, []);

  const deleteMovie = async (id) => {
    if(window.confirm("Are you sure you want to delete this movie?")) {
      try {
        await API.delete(`movies/${id}/`);
        fetchMovies();
      } catch (err) { console.error(err); }
    }
  };

  return (
    <div className="container">
      <div className="header-row">
        <h2>Movies</h2>
        <button className="add-btn" onClick={() => setShowForm(!showForm)}>
          {showForm ? "Close" : "Add Movie"}
        </button>
      </div>

      {showForm && <MovieForm />}

      <div className="movie-grid">
        {movies.map(movie => (
          <div className="movie-card" key={movie.id}>
            <Link to={`/movie/${movie.id}`}>
              {movie.poster ? (
                <img src={movie.poster} alt={movie.title} />
              ) : (
                <div className="no-image">No Image</div>
              )}
              <h3>{movie.title}</h3>
            </Link>
            <p>‚≠ê {movie.rating}</p>
            <button className="delete-btn" onClick={() => deleteMovie(movie.id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
}
