import React, { useEffect, useState } from "react";
import API from "../api";
import { Link } from "react-router-dom";
import '../styles/MovieList.css';

export default function MovieList() {
  const [movies, setMovies] = useState([]);

  const fetchMovies = async () => {
    try {
      const res = await API.get("movies/");
      // Check if data has 'results' (paginated)
      const movieData = res.data.results ? res.data.results : res.data;
      setMovies(movieData);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchMovies();
    const handler = () => fetchMovies();
    window.addEventListener("movie:added", handler);
    return () => window.removeEventListener("movie:added", handler);
  }, []);

  return (
    <div className="movie-list">
      <div className="grid">
        {movies.map((movie) => (
          <div className="card" key={movie.id}>
            <Link to={`/movie/${movie.id}`}>
              {movie.poster ? (
                <img src={`http://127.0.0.1:8000${movie.poster}`} alt={movie.title} />
              ) : (
                <div className="placeholder">No Image</div>
              )}
              <h3>{movie.title}</h3>
            </Link>
            <p>Rating: {movie.rating || "N/A"}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
